scripts.fix:declarations:
mkdir -p dist/components && cp packages/web-browser/dist/src/index.d.ts dist/index.d.ts && cp packages/web-browser/dist/components/*.d.ts dist/components/ 2>/dev/null || true && cp packages/web-browser/dist/types.d.ts dist/types.d.ts 2>/dev/null || true && cp packages/web-browser/dist/App.d.ts dist/App.d.ts 2>/dev/null || true && sed -i 's/..\//.\//g' dist/index.d.ts || echo '// Core exports for @taskwizer/web-browser package

// Components
export { BrowserView } from "./components/BrowserView";
export { TabComponent } from "./components/Tab";

// Types
export { Tab, TabGroup, ConversationMessage, GeminiSearchResult, Bookmark, ContextMenuAction } from "./types";

// App and configuration
export { default as WebBrowserApp } from "./App";

export interface WebBrowserConfig {
    enableApi?: boolean;
    apiBasePath?: string;
    standalone?: boolean;
    defaultUrl?: string;
    enablePdf?: boolean;
    enableEpub?: boolean;
    maxFileSize?: number;
}

export declare function createWebBrowserConfig(overrides?: Partial<WebBrowserConfig>): WebBrowserConfig;' > dist/index.d.ts
